package com.github.artislong.annotation;import java.lang.annotation.*;/** * 定义Jfinal Controller路由,后续增加JFinal对应的路由归类等功能 * @author chenmin */@Target({ElementType.TYPE})@Retention(RetentionPolicy.RUNTIME)@Documented@Inheritedpublic @interface RouterPath {    /**     * 路由值     * @return     */    String value() default "";}