/** * $Id: JfinalProperties.java,v 1.0 2019-07-14 00:46 chenmin Exp $ */package com.github.jfinal.config;import com.google.common.collect.Maps;import com.jfinal.plugin.activerecord.dialect.Dialect;import com.jfinal.render.ViewType;import lombok.Data;import lombok.experimental.Accessors;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.InitializingBean;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.util.ObjectUtils;import java.util.List;import java.util.Map;/** * @author 陈敏 * @version $Id: JfinalProperties.java,v 1.1 2019-07-14 00:46 chenmin Exp $ * Created on 2019-07-14 00:46 * My blog： https://www.chenmin.info */@Slf4j@Data@Accessors(chain = true)@ConfigurationProperties(prefix = "jfinal")public class JfinalProperties implements InitializingBean {    /**     * SQL模板路径     */    private List<String> sqlTemplates;    /**     * kit类全路径     */    private List<String> kitClasses;    /**     * 是否打开开发模式     */    private Boolean devMode = false;    /**     * 是否显示查询SQL     */    private Boolean showSql;    /**     * 数据库方言:默认支持MYSQL,ORACLE,SQLSERVER,SQLITE,POSTGRESQL     */    private Class<? extends Dialect> dialect;    private Boolean injectDependency = false;    private String baseUploadPath;    private String baseDownloadPath;    private Integer maxPostSize;    private Integer delayInSeconds;    private String defaultBaseName;    private String defaultLocale;    private Boolean injectSuperClass;    private Boolean reportAfterInvocation;    private String datePattern;    private String urlParaSeparator;    private String viewExtension;    private ViewType viewType;    private Boolean clearAfterMapping;    private Boolean mappingSuperClass;    private Boolean createSession;    private String baseTemplatePath;    private Boolean sessionInView;    /**     * 是否允许覆盖Request     */    private Boolean allowRequestOverride;    /**     * 是否允许覆盖Session     */    private Boolean allowSessionOverride;    /**     * 事务级别     */    private Integer transactionLevel;    /**     * 插件加载顺序     */    private Integer configPluginOrder;    /**     * Error View映射     */    private Map<Integer, String> errorViewMapping = Maps.newHashMap();    /**     * jfinal定制任务配置     */    private Map<String, TaskInfo> taskInfoMap = Maps.newHashMap();    @Data    @Accessors(chain = true)    public static class TaskInfo {        /**         * 时间表达式         */        private String cron;        /**         * 任务类         */        private String task;        /**         * 是否开启守护进程         */        private Boolean daemon;        /**         * 是否开启任务,默认开启         */        private Boolean enable = true;        public TaskInfo() {        }        public TaskInfo(String cron, String task, boolean daemon, boolean enable) {            if (ObjectUtils.isEmpty(cron)) {                throw new IllegalArgumentException("cron 不能为空.");            }            if (ObjectUtils.isEmpty(task)) {                throw new IllegalArgumentException("task 不能为 null.");            }            this.cron = cron.trim();            this.task = task;            this.daemon = daemon;            this.enable = enable;        }    }    @Override    public void afterPropertiesSet() {        if (!ObjectUtils.isEmpty(this.taskInfoMap)) {            taskInfoMap.forEach((key, taskInfo) -> {                if (ObjectUtils.isEmpty(taskInfo.getTask())) {                    throw new IllegalArgumentException("task must be not null");                }                if (ObjectUtils.isEmpty(taskInfo.getCron())) {                    throw new IllegalArgumentException("cron must be not null");                }                if (ObjectUtils.isEmpty(taskInfo.getDaemon())) {                    log.warn("daemon is null");                }            });        }        if (ObjectUtils.isEmpty(this.kitClasses)) {            throw new IllegalArgumentException("kitClasses must be not null");        }    }}